// ECharts 레이더 차트 - 간단한 버전 (데이터 기반)
// $data.series에서 데이터를 가져옵니다
const series = $data.series || [];

// 각 시리즈의 마지막 값을 가져옵니다
const getValue = (refId) => {
  const s = series.find(ser => ser.refId === refId);
  if (s && s.fields && s.fields[0] && s.fields[0].values) {
    const values = s.fields[0].values;
    return values[values.length - 1] || 0;
  }
  return 0;
};

const cpuValue = Math.min(getValue('A') || 50, 100);
const memValue = Math.min(getValue('B') || 60, 100);
const diskValue = Math.min(getValue('C') || 40, 100);
const netValue = Math.min((getValue('D') || 30) * 10, 100); // 네트워크는 스케일 조정
const loadValue = Math.min((getValue('E') || 2) * 20, 100); // 로드는 스케일 조정

